public class Ejercicio7 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
//Sin utilizar la sentencia FOR hacer un listado de 10 al -10 y seguido otro de -10 a 10.
		
		int i=10;
		while(i>=-10) {
			System.out.println(i);
			i=i-1;
		}
		
		int j=-10;
		while(j<=10) {
			System.out.println(j);
			j=j+1;//j++;
		}
	}

}
