package Operadores;

/**
 * 
 */
public class Ejemplo2 {


	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		int b=10;
		boolean esmayor = (a>b);
		boolean esigual = (a==b);
		boolean esDistinto = (a!=b);

		System.out.println("¿a es mayor?" + esmayor);
		System.out.println("¿son iguales?" + esigual);
		System.out.println("¿son distintos?" + esDistinto);
	}

}
